import React from 'react';
import './App.css';

class App extends React.Component {

  rotateImage() {
			let image1 = document.getElementById("image1");
      let image2 = document.getElementById("image2");
      let image3 = document.getElementById("image3");
      let image4 = document.getElementById("image4");
      let angle = document.getElementById("angle").value;
      angle %= 360;
      image1.style = 'transform: rotate(' + angle + 'deg)';
/*       image2.style = 'transform: rotate(' + angle + 'deg) ; transform-origin: bottom left';
      image3.style = 'transform: rotate(' + angle + 'deg) ; transform-origin: top right';
      image4.style = 'transform: rotate(' + angle + 'deg) ; transform-origin: top left'; */	
  }

  render() {
    return (
      <div className="App">
          <div className="inputTag">
              Enter the angle: <input type="text" name="angle" id="angle"/>
              <button className="button" onClick={this.rotateImage}>Rotate</button>
          </div>
          <div className="container">
              <div className="row">
                <div className="column">
                    <img style={{transformOrigin: "bottom right"}} src="https://i.postimg.cc/FfpJBC0y/image-part-001.jpg" alt="Part1" id="image1" />
                </div>
                <div className="column">
                    <img src="https://i.postimg.cc/QVwKwTKy/image-part-002.jpg" alt="Part2" id="image2" />
                </div>
            </div>
            <div className="row">
                <div className="column">
                    <img src="https://i.postimg.cc/njw9HsRM/image-part-003.jpg" alt="Part3" id="image3" />
                </div>
                <div className="column">
                    <img src="https://i.postimg.cc/4nmhW5yc/image-part-004.jpg" alt="Part4" id="image4" />
                </div>
            </div>
          </div>
      </div>
    );
  }

}

export default App;
